sql.data:
SELECT * FROM ( WITH PRSS AS ( SELECT SUBSTR(PRSS, INSTR(PRSS,'|',1,2) + 1, INSTR(PRSS,'|',1,3) - INSTR(PRSS,'|',1,2) -1) PRSS1 , SUBSTR(PRSS, INSTR(PRSS,'|',1,3) + 1, INSTR(PRSS,'|',1,4) - INSTR(PRSS,'|',1,3) -1) PRSS2 , SUBSTR(PRSS, INSTR(PRSS,'|',1,4) + 1, INSTR(PRSS,'|',1,5) - INSTR(PRSS,'|',1,4) -1) PRSS3 , SUBSTR(PRSS, INSTR(PRSS,'|',1,5) + 1) PRSS4 , SUBSTR(PRSSNM, INSTR(PRSSNM,'|',1,2) + 1, INSTR(PRSSNM,'|',1,3) - INSTR(PRSSNM,'|',1,2) -1) PRSSNM1 , SUBSTR(PRSSNM, INSTR(PRSSNM,'|',1,3) + 1, INSTR(PRSSNM,'|',1,4) - INSTR(PRSSNM,'|',1,3) -1) PRSSNM2 , SUBSTR(PRSSNM, INSTR(PRSSNM,'|',1,4) + 1, INSTR(PRSSNM,'|',1,5) - INSTR(PRSSNM,'|',1,4) -1) PRSSNM3 , SUBSTR(PRSSNM, INSTR(PRSSNM,'|',1,5) + 1) PRSSNM4 , GRP_ORG_C FROM ( SELECT SYS_CONNECT_BY_PATH(BSN_PRSS_C,'|') AS PRSS , SYS_CONNECT_BY_PATH(BSN_PRSNM,'|') AS PRSSNM , GRP_ORG_C FROM TB_OR_OC_PRSS WHERE LVL_NO='4' AND VLD_YN='Y' START WITH UP_BSN_PRSS_C IS NULL CONNECT BY PRIOR BSN_PRSS_C = UP_BSN_PRSS_C AND PRIOR GRP_ORG_C = GRP_ORG_C ) ) , DEPT_BRNM AS ( SELECT A.GRP_ORG_C , B.UP_BRC , C.BRNM , B.BRC FROM TB_OR_RM_RKP A LEFT JOIN TB_OR_OM_ORGZ B ON A.GRP_ORG_C = B.GRP_ORG_C AND A.BRC = B.BRC AND B.UYN = 'Y' LEFT JOIN TB_OR_OM_ORGZ C ON B.UP_BRC = C.BRC AND C.UYN = 'Y' GROUP BY A.GRP_ORG_C,B.UP_BRC,C.BRNM,B.BRC )
SELECT B.GRP_ORG_C , B.BRC , A.OPRK_RKP_ID RKP_ID , CASE WHEN A.RKP_TPC ='01' THEN '전사' WHEN A.RKP_TPC ='03' THEN '영업점' WHEN A.RKP_TPC ='02' THEN C.BRNM END DEPT_BRNM , CASE WHEN A.RKP_TPC ='01' THEN '전사' WHEN A.RKP_TPC ='03' THEN '영업점' WHEN A.RKP_TPC ='02' THEN B.BRNM END BRNM , D.PRSSNM1 , D.PRSSNM2 , D.PRSSNM3 , D.PRSSNM4 , A.RK_ISC_CNTN , '' C1 , TO_CHAR(A.FIR_INP_DTM,'YYYYMMDD') REG_DT , TO_CHAR(A.LSCHG_DTM,'YYYYMMDD') CHG_DT FROM TB_OR_RM_RKP A , TB_OR_OM_ORGZ B , DEPT_BRNM C , PRSS D , TB_OR_RH_CTL_SCF E , TB_OR_RH_REL_LSS F WHERE A.BRC = B.BRC AND A.OPRK_RKP_ID = F.OPRK_RKP_ID AND C.BRC = B.BRC AND A.OPRK_RKP_ID = E.OPRK_RKP_ID AND A.BSN_PRSS_C = D.PRSS4 AND F.LSHP_AMNNO = TRIM('1')
)
